@import url("/css/?s=41577");
body {
	background-color: #888888;
	  background-image: linear-gradient(silver, #535353);
  background: silver;
  background: linear-gradient(silver, #535353);
}

.spoiler.open {
background-color: #ffffff;
}

.spoiler-wrapper {
    width: 640px;
    overflow-x: visible;
    display: flex;
}

.spoiler-wrapper .spoiler {
    height: fit-content;
    min-height: 640px;
    width: 27px;
    box-sizing: border-box;
    display: inline-flex;
    margin-right: 2px;
    flex-shrink: 0;
    overflow: visible;
}

.spoiler-wrapper .spoiler.open {
    width: fit-content;
}

.spoiler-wrapper .spoiler img {
    max-width: none;
}

.spoiler-wrapper .spoiler div:first-child {
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 2px;
}

.spoiler-wrapper .spoiler div:first-child input {
    transform: rotate(-90deg);
}

.spoiler-wrapper .spoiler div:last-child {
    display: block;
    visibility: hidden;
}

.spoiler-wrapper .spoiler.open div:last-child {
    visibility: visible;
}

